import './App.css';
import React from 'react';
import { Redirect } from 'react-router'
// import WhoisAPI from './whois-api'

// let whoisApi = new WhoisAPI();

function App() {
  return (
    <div className="App">
      <header className="App-header"> 
       <SearchForm />
      </header>
    </div>
  );
}

function getDomainInfo(domain) {
  const request = new XMLHttpRequest();
  request.open("GET", `localhost:3001/lookup/${domain}`);
  request.send();
  console.log(request.responseText);
}

class SearchForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
      redirect: false
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    getDomainInfo(event.target.value)
    this.setState({ redirect: true });
    event.preventDefault();
  }

  render() {
    const { redirect } = this.state
    return (
      <form action={this.handleSubmit}>
        <label>
          WHOIS IP or Domain Lookup: <br/>
          <input type="text" name="domain" placeholder="e.g. www.google.com..."/>
        </label>
        <input type="submit" value="Search"/>
      </form>
    )
  }
}

export default App;
