import './App.css';
import React from 'react';
import WhoisAPI from './whois-api';

function App() {
  return (
    <div className="App">
      <header className="App-header"> 
       <SearchForm />
      </header>
    </div>
  );
}

class Results extends React.Component {
  constructor(props) {
    super(props);
    this.data = this.props.data;
  }
}

class SearchForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
      responseData: false
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    WhoisAPI.getDomainInfo(this.state.value)
      .then(data => this.setState({responseData:data}))
    event.preventDefault();
  }

  displayOutput(data) {

  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <label for="domain">
            WHOIS IP or Domain Lookup: <br/>
          </label>
          <input type="text" name="domain" onChange={this.handleChange} value={this.state.value} placeholder="e.g. www.google.com..."/>
          <input type="submit"/>
        </form>
        {this.state.responseData ? 
          <div>
            {
              Object.keys(this.state.responseData).map((e, i) => {
                <div key={i} {...e} />
              })
            }
          </div>
          :
          <div> 
            <br/>
            Your search results will appear here    
          </div>
        }
      </div>
    )
  }
}

export default App;
