const { RESTDataSource } = require('apollo-datasource-http');

class WhoisAPI extends RESTDataSource {
  constructor() {
    super();
    this.baseURL = 'localhost:5001/';
  }

  async getDomainInfo(domain) {
    // Send a GET request to the specified endpoint
    return this.get(`lookup/${domain}`);
  }
}
