import './App.css';
import WhoisAPI from './whois-api'

function App() {
  return (
    <div className="App">
      <header className="App-header"> 
       { searchBox() } 
      </header>
    </div>
  );
}


class SearchForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {value: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handlSubmit(event) {
    event.preventDefault();
  }

  render() {
    return (
      <form action={whois.getDomainInfo()}>
        <label>
          WHOIS IP or Domain Lookup: <br/>
          <input type="text" name="domain" placeholder="e.g. www.google.com..."/>
        </label>
        <button type="submit">Search</button>
      </form>
    )
  }
}

export default App;
